import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useRoute, useLocation } from "wouter";
import { ThumbsUp, ThumbsDown, Share2, Clock, Send, Edit, Trash, Reply, Heart, ChevronDown, ChevronUp, MoreVertical } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Separator } from "@/components/ui/separator";
import { Textarea } from "@/components/ui/textarea";
import { useAppStore } from "@/store/useAppStore";
import { useToast } from "@/hooks/use-toast";
import { VideoWithChannel, Comment } from "@shared/schema";
import { useState, useRef } from "react";
import { formatDistanceToNow } from "date-fns";
import CustomVideoPlayer from "@/components/CustomVideoPlayer";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";

interface CommentWithUser extends Comment {
  user?: {
    id: string;
    username: string | null;
    profileImageUrl: string | null;
  };
  replies?: CommentWithUser[];
}

export default function Watch() {
  const [, params] = useRoute("/watch/:id");
  const [, setLocation] = useLocation();
  const videoId = params?.id;
  const { currentUserId } = useAppStore();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const videoRef = useRef<HTMLVideoElement>(null);
  const [hasTrackedView, setHasTrackedView] = useState(false);
  const [descriptionExpanded, setDescriptionExpanded] = useState(false);

  const [commentText, setCommentText] = useState("");
  const [replyTo, setReplyTo] = useState<string | null>(null);
  const [replyText, setReplyText] = useState("");
  const [editingComment, setEditingComment] = useState<string | null>(null);
  const [editText, setEditText] = useState("");
  const [saveDialogOpen, setSaveDialogOpen] = useState(false);
  const [newPlaylistName, setNewPlaylistName] = useState("");

  const { data: video, isLoading } = useQuery<VideoWithChannel>({
    queryKey: ["/api/videos", videoId],
    queryFn: async () => {
      const response = await fetch(`/api/videos/${videoId}`);
      if (!response.ok) throw new Error('Failed to fetch video');
      return response.json();
    },
    enabled: !!videoId
  });

  const { data: likeCounts = { likes: 0, dislikes: 0 } } = useQuery({
    queryKey: ["/api/videos/likes", videoId],
    queryFn: async () => {
      const response = await fetch(`/api/videos/${videoId}/likes`);
      if (!response.ok) return { likes: 0, dislikes: 0 };
      return response.json();
    },
    enabled: !!videoId
  });

  const { data: userLike } = useQuery({
    queryKey: ["/api/videos/user-like", videoId, currentUserId],
    queryFn: async () => {
      const response = await fetch(`/api/videos/${videoId}/user-like/${currentUserId}`);
      if (!response.ok) return null;
      return response.json();
    },
    enabled: !!videoId && !!currentUserId
  });

  const { data: subscriptions = [] } = useQuery({
    queryKey: ["/api/subscriptions", currentUserId],
    queryFn: async () => {
      const response = await fetch(`/api/subscriptions/${currentUserId}`);
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!currentUserId
  });

  const isSubscribed = subscriptions.some((sub: any) => sub.channelId === video?.channelId);

  const { data: comments = [] } = useQuery<CommentWithUser[]>({
    queryKey: ["/api/videos/comments", videoId],
    queryFn: async () => {
      const response = await fetch(`/api/videos/${videoId}/comments`);
      if (!response.ok) return [];
      const data = await response.json();

      const topLevelComments = data.filter((c: Comment) => !c.parentId);
      const replies = data.filter((c: Comment) => c.parentId);

      return topLevelComments.map((comment: Comment) => ({
        ...comment,
        replies: replies.filter((r: Comment) => r.parentId === comment.id)
      }));
    },
    enabled: !!videoId
  });

  const { data: relatedVideos = [] } = useQuery<VideoWithChannel[]>({
    queryKey: ["/api/videos/related", videoId],
    queryFn: async () => {
      const response = await fetch('/api/videos');
      if (!response.ok) return [];
      const allVideos = await response.json();
      return allVideos.filter((v: VideoWithChannel) => v.id !== videoId).slice(0, 15);
    },
    enabled: !!videoId
  });

  const trackViewMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/videos/${videoId}/view`, { method: 'POST' });
      if (!response.ok) throw new Error('Failed to track view');
      return response.json();
    }
  });

  const likeMutation = useMutation({
    mutationFn: async (type: 'like' | 'dislike') => {
      const response = await fetch(`/api/videos/${videoId}/like`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ type })
      });
      if (!response.ok) {
        if (response.status === 401 || response.status === 403) {
          throw new Error('UNAUTHORIZED');
        }
        throw new Error('Failed to like/dislike');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/videos/likes", videoId] });
      queryClient.invalidateQueries({ queryKey: ["/api/videos/user-like", videoId, currentUserId] });
    },
    onError: (error: Error) => {
      if (error.message === 'UNAUTHORIZED') {
        toast({ 
          title: "Authentication required", 
          description: "Please log in to like videos", 
          variant: "destructive" 
        });
      } else {
        toast({ 
          title: "Error", 
          description: "Failed to like/dislike video", 
          variant: "destructive" 
        });
      }
    }
  });

  const subscribeMutation = useMutation({
    mutationFn: async () => {
      if (isSubscribed) {
        const response = await fetch('/api/subscriptions', {
          method: 'DELETE',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ channelId: video?.channelId })
        });
        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            throw new Error('UNAUTHORIZED');
          }
          throw new Error('Failed to unsubscribe');
        }
      } else {
        const response = await fetch('/api/subscriptions', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ channelId: video?.channelId })
        });
        if (!response.ok) {
          if (response.status === 401 || response.status === 403) {
            throw new Error('UNAUTHORIZED');
          }
          throw new Error('Failed to subscribe');
        }
        return response.json();
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subscriptions", currentUserId] });
      toast({
        title: isSubscribed ? "Unsubscribed" : "Subscribed",
        description: isSubscribed 
          ? `Unsubscribed from ${video?.channel.name}` 
          : `Subscribed to ${video?.channel.name}`
      });
    },
    onError: (error: Error) => {
      if (error.message === 'UNAUTHORIZED') {
        toast({ 
          title: "Authentication required", 
          description: "Please log in to manage subscriptions", 
          variant: "destructive" 
        });
      } else {
        toast({ 
          title: "Error", 
          description: isSubscribed ? "Failed to unsubscribe" : "Failed to subscribe", 
          variant: "destructive" 
        });
      }
    }
  });

  const createCommentMutation = useMutation({
    mutationFn: async (data: { content: string; parentId?: string }) => {
      const response = await fetch(`/api/videos/${videoId}/comments`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          content: data.content,
          parentId: data.parentId || null
        })
      });
      if (!response.ok) {
        if (response.status === 401 || response.status === 403) {
          throw new Error('UNAUTHORIZED');
        }
        throw new Error('Failed to post comment');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/videos/comments", videoId] });
      setCommentText("");
      setReplyTo(null);
      setReplyText("");
      toast({ title: "Comment posted" });
    },
    onError: (error: Error) => {
      if (error.message === 'UNAUTHORIZED') {
        toast({ 
          title: "Authentication required", 
          description: "Please log in to comment", 
          variant: "destructive" 
        });
      } else {
        toast({ 
          title: "Error", 
          description: "Failed to post comment", 
          variant: "destructive" 
        });
      }
    }
  });

  const updateCommentMutation = useMutation({
    mutationFn: async (data: { id: string; content: string }) => {
      const response = await fetch(`/api/comments/${data.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ content: data.content })
      });
      if (!response.ok) {
        if (response.status === 401 || response.status === 403) {
          throw new Error('UNAUTHORIZED');
        }
        throw new Error('Failed to update comment');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/videos/comments", videoId] });
      setEditingComment(null);
      setEditText("");
      toast({ title: "Comment updated" });
    },
    onError: (error: Error) => {
      if (error.message === 'UNAUTHORIZED') {
        toast({ 
          title: "Authentication required", 
          description: "Please log in to edit comments", 
          variant: "destructive" 
        });
      } else {
        toast({ 
          title: "Error", 
          description: "Failed to update comment", 
          variant: "destructive" 
        });
      }
    }
  });

  const deleteCommentMutation = useMutation({
    mutationFn: async (commentId: string) => {
      const response = await fetch(`/api/comments/${commentId}`, { method: 'DELETE' });
      if (!response.ok) {
        if (response.status === 401 || response.status === 403) {
          throw new Error('UNAUTHORIZED');
        }
        throw new Error('Failed to delete comment');
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/videos/comments", videoId] });
      toast({ title: "Comment deleted" });
    },
    onError: (error: Error) => {
      if (error.message === 'UNAUTHORIZED') {
        toast({ 
          title: "Authentication required", 
          description: "Please log in to delete comments", 
          variant: "destructive" 
        });
      } else {
        toast({ 
          title: "Error", 
          description: "Failed to delete comment", 
          variant: "destructive" 
        });
      }
    }
  });

  const likeCommentMutation = useMutation({
    mutationFn: async (commentId: string) => {
      const response = await fetch(`/api/comments/${commentId}/like`, { method: 'POST' });
      if (!response.ok) {
        if (response.status === 401 || response.status === 403) {
          throw new Error('UNAUTHORIZED');
        }
        throw new Error('Failed to like comment');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/videos/comments", videoId] });
    },
    onError: (error: Error) => {
      if (error.message === 'UNAUTHORIZED') {
        toast({ 
          title: "Authentication required", 
          description: "Please log in to like comments", 
          variant: "destructive" 
        });
      } else {
        toast({ 
          title: "Error", 
          description: "Failed to like comment", 
          variant: "destructive" 
        });
      }
    }
  });

  const { data: userPlaylists = [] } = useQuery({
    queryKey: ["/api/playlists", currentUserId],
    queryFn: async () => {
      if (!currentUserId) return [];
      const response = await fetch(`/api/playlists/${currentUserId}`);
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!currentUserId
  });

  const createPlaylistMutation = useMutation({
    mutationFn: async (name: string) => {
      const response = await fetch('/api/playlists', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, isPublic: false })
      });
      if (!response.ok) throw new Error('Failed to create playlist');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/playlists", currentUserId] });
      setNewPlaylistName("");
      toast({ title: "Playlist created!" });
    },
    onError: () => {
      toast({ 
        title: "Error", 
        description: "Failed to create playlist", 
        variant: "destructive" 
      });
    }
  });

  const addToPlaylistMutation = useMutation({
    mutationFn: async (playlistId: string) => {
      const response = await fetch(`/api/playlists/${playlistId}/videos`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          videoId: videoId,
          position: 0
        })
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to add to playlist');
      }
      return response.json();
    },
    onSuccess: () => {
      setSaveDialogOpen(false);
      toast({ 
        title: "Saved!", 
        description: "Video added to playlist" 
      });
    },
    onError: (error: Error) => {
      toast({ 
        title: "Error", 
        description: error.message || "Failed to add video to playlist", 
        variant: "destructive" 
      });
    }
  });

  const handleVideoPlay = () => {
    if (!hasTrackedView && videoId) {
      trackViewMutation.mutate();
      setHasTrackedView(true);
    }
  };

  const handleLike = () => {
    if (!currentUserId) {
      toast({ title: "Please log in", description: "You must be logged in to like videos", variant: "destructive" });
      return;
    }
    likeMutation.mutate('like');
  };

  const handleDislike = () => {
    if (!currentUserId) {
      toast({ title: "Please log in", description: "You must be logged in to dislike videos", variant: "destructive" });
      return;
    }
    likeMutation.mutate('dislike');
  };

  const handleSubscribe = () => {
    if (!currentUserId) {
      toast({ title: "Please log in", description: "You must be logged in to subscribe", variant: "destructive" });
      return;
    }
    subscribeMutation.mutate();
  };

  const handleShare = () => {
    const videoUrl = window.location.href;
    navigator.clipboard.writeText(videoUrl).then(() => {
      toast({
        title: "Link copied!",
        description: "Video link has been copied to clipboard",
      });
    }).catch(() => {
      toast({
        title: "Failed to copy",
        description: "Could not copy link to clipboard",
        variant: "destructive"
      });
    });
  };

  const handleSave = () => {
    if (!currentUserId) {
      toast({ title: "Please log in", description: "You must be logged in to save videos", variant: "destructive" });
      return;
    }
    setSaveDialogOpen(true);
  };

  const handleCreatePlaylist = () => {
    if (!newPlaylistName.trim()) return;
    createPlaylistMutation.mutate(newPlaylistName);
  };

  const handlePostComment = () => {
    if (!currentUserId) {
      toast({ title: "Please log in", description: "You must be logged in to comment", variant: "destructive" });
      return;
    }
    if (!commentText.trim()) return;
    createCommentMutation.mutate({ content: commentText });
  };

  const handlePostReply = (parentId: string) => {
    if (!currentUserId) {
      toast({ title: "Please log in", description: "You must be logged in to reply", variant: "destructive" });
      return;
    }
    if (!replyText.trim()) return;
    createCommentMutation.mutate({ content: replyText, parentId });
  };

  const handleEditComment = (commentId: string) => {
    if (!editText.trim()) return;
    updateCommentMutation.mutate({ id: commentId, content: editText });
  };

  const handleChannelClick = () => {
    if (video?.channelId) {
      setLocation(`/channel/${video.channelId}`);
    }
  };

  const handleDeleteComment = (commentId: string) => {
    if (window.confirm("Are you sure you want to delete this comment?")) {
      deleteCommentMutation.mutate(commentId);
    }
  };

  const handleLikeComment = (commentId: string) => {
    if (!currentUserId) {
      toast({ title: "Please log in", description: "You must be logged in to like comments", variant: "destructive" });
      return;
    }
    likeCommentMutation.mutate(commentId);
  };

  const handleRelatedVideoClick = (videoId: string) => {
    setLocation(`/watch/${videoId}`);
  };

  if (isLoading || !video) {
    return (
      <div className="max-w-[1800px] mx-auto px-3 sm:px-4 py-4 sm:py-6">
        <div className="grid grid-cols-1 xl:grid-cols-[1fr,400px] gap-4 sm:gap-6">
          <div className="space-y-3 sm:space-y-4">
            <div className="aspect-video bg-muted animate-pulse rounded-2xl"></div>
            <div className="h-6 sm:h-8 bg-muted animate-pulse rounded-lg w-3/4"></div>
            <div className="flex gap-3 sm:gap-4">
              <div className="w-10 h-10 sm:w-12 sm:h-12 bg-muted animate-pulse rounded-full"></div>
              <div className="flex-1 space-y-2 sm:space-y-3">
                <div className="h-3 sm:h-4 bg-muted animate-pulse rounded-lg w-1/4"></div>
                <div className="h-2 sm:h-3 bg-muted animate-pulse rounded-lg w-1/6"></div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const CommentItem = ({ comment, isReply = false }: { comment: CommentWithUser; isReply?: boolean }) => {
    const isOwner = comment.userId === currentUserId;
    const isEditing = editingComment === comment.id;
    const isReplying = replyTo === comment.id;

    return (
      <div className={`flex gap-3 sm:gap-4 ${isReply ? 'ml-10 sm:ml-14 mt-2 sm:mt-3' : 'py-3 sm:py-4'}`}>
        <Avatar className="h-9 w-9 sm:h-10 sm:w-10 flex-shrink-0 ring-2 ring-background">
          <AvatarImage src={comment.user?.profileImageUrl || undefined} />
          <AvatarFallback className="bg-gradient-to-br from-blue-500 to-purple-600 text-white">
            {comment.user?.username?.[0]?.toUpperCase() || 'U'}
          </AvatarFallback>
        </Avatar>
        <div className="flex-1 space-y-1.5 sm:space-y-2 min-w-0">
          <div>
            <div className="flex items-baseline gap-2 mb-1 flex-wrap">
              <span className="font-semibold text-sm hover:text-primary cursor-pointer transition-colors">
                @{comment.user?.username || 'Anonymous'}
              </span>
              <span className="text-xs text-muted-foreground">
                {comment.createdAt ? formatDistanceToNow(new Date(comment.createdAt), { addSuffix: true }) : 'just now'}
              </span>
            </div>

            {isEditing ? (
              <div className="space-y-3">
                <Textarea
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                  className="min-h-[80px] resize-none border-2 focus-visible:ring-2 focus-visible:ring-primary"
                />
                <div className="flex gap-2">
                  <Button 
                    size="sm" 
                    onClick={() => handleEditComment(comment.id)}
                    disabled={updateCommentMutation.isPending}
                    className="rounded-full"
                  >
                    {updateCommentMutation.isPending ? "Saving..." : "Save"}
                  </Button>
                  <Button 
                    size="sm" 
                    variant="ghost" 
                    onClick={() => {
                      setEditingComment(null);
                      setEditText("");
                    }}
                    className="rounded-full"
                  >
                    Cancel
                  </Button>
                </div>
              </div>
            ) : (
              <p className="text-sm leading-relaxed break-words">{comment.content}</p>
            )}
          </div>

          {!isEditing && (
            <div className="flex items-center gap-2 flex-wrap">
              <Button
                variant="ghost"
                size="sm"
                className="h-9 px-3 rounded-full hover:bg-primary/10 transition-all"
                onClick={() => handleLikeComment(comment.id)}
                disabled={likeCommentMutation.isPending}
              >
                <Heart className="h-4 w-4 mr-1.5" />
                <span className="text-xs font-medium">{comment.likes || 0}</span>
              </Button>

              {!isReply && (
                <Button
                  variant="ghost"
                  size="sm"
                  className="h-9 px-3 rounded-full hover:bg-primary/10 transition-all"
                  onClick={() => {
                    setReplyTo(comment.id);
                    setReplyText("");
                  }}
                >
                  <Reply className="h-4 w-4 mr-1.5" />
                  <span className="text-xs font-medium">Reply</span>
                </Button>
              )}

              {isOwner && (
                <>
                  <Button
                    variant="ghost"
                    size="sm"
                    className="h-9 px-3 rounded-full hover:bg-primary/10 transition-all"
                    onClick={() => {
                      setEditingComment(comment.id);
                      setEditText(comment.content);
                    }}
                  >
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    className="h-9 px-3 rounded-full hover:bg-destructive/10 text-destructive transition-all"
                    onClick={() => handleDeleteComment(comment.id)}
                    disabled={deleteCommentMutation.isPending}
                  >
                    <Trash className="h-4 w-4" />
                  </Button>
                </>
              )}
            </div>
          )}

          {isReplying && (
            <div className="space-y-3 mt-3 bg-muted/30 rounded-xl p-4">
              <Textarea
                value={replyText}
                onChange={(e) => setReplyText(e.target.value)}
                placeholder="Add a reply..."
                className="min-h-[80px] resize-none bg-background"
              />
              <div className="flex gap-2 justify-end">
                <Button 
                  size="sm" 
                  variant="ghost"
                  onClick={() => {
                    setReplyTo(null);
                    setReplyText("");
                  }}
                  className="rounded-full"
                >
                  Cancel
                </Button>
                <Button 
                  size="sm" 
                  onClick={() => handlePostReply(comment.id)}
                  disabled={createCommentMutation.isPending || !replyText.trim()}
                  className="rounded-full"
                >
                  <Send className="h-4 w-4 mr-2" />
                  {createCommentMutation.isPending ? "Posting..." : "Reply"}
                </Button>
              </div>
            </div>
          )}

          {comment.replies && comment.replies.length > 0 && (
            <div className="space-y-1 mt-4">
              {comment.replies.map((reply) => (
                <CommentItem key={reply.id} comment={reply} isReply />
              ))}
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="max-w-[1800px] mx-auto px-0 sm:px-3 md:px-4 py-0 sm:py-4 md:py-6 w-full overflow-x-hidden">
      <div className="grid grid-cols-1 xl:grid-cols-[1fr,400px] gap-0 sm:gap-4 md:gap-6 w-full">
        {/* Main Content */}
        <div className="space-y-0 sm:space-y-5">
          {/* Video Player */}
          <div className="aspect-video bg-black sm:rounded-2xl overflow-hidden shadow-2xl ring-1 ring-white/10">
            <CustomVideoPlayer
              src={video.videoUrl}
              videoRef={videoRef}
              onPlay={handleVideoPlay}
              onError={(e) => {
                console.error("Video error:", e);
                toast({ 
                  title: "Video error", 
                  description: "Failed to load video. Using placeholder.", 
                  variant: "destructive" 
                });
              }}
            />
          </div>

          {/* Video Info Section - Mobile Optimized */}
          <div className="px-3 sm:px-0 py-3 sm:py-0 space-y-3">
            {/* Video Title */}
            <h1 className="text-lg sm:text-lg md:text-xl lg:text-2xl font-bold leading-tight hover:text-primary/90 transition-colors cursor-default break-words">
              {video.title}
            </h1>

            {/* Views and Date - Compact Line (Mobile Only) */}
            <div className="flex items-center gap-1 text-sm text-muted-foreground sm:hidden">
              <span className="font-medium">{video.views?.toLocaleString()} views</span>
              <span>â€¢</span>
              <span>
                {formatDistanceToNow(new Date(video.uploadedAt || Date.now()), { addSuffix: true })}
              </span>
            </div>

            {/* Channel Section - Single Row on Mobile */}
            <div className="flex items-center justify-between gap-3 w-full">
              <div className="flex items-center gap-3 flex-1 min-w-0">
                <Avatar className="h-10 w-10 sm:h-12 sm:w-12 flex-shrink-0 ring-2 ring-background cursor-pointer hover:ring-primary transition-all" onClick={handleChannelClick}>
                  <AvatarImage src={video.channel.avatar || undefined} />
                  <AvatarFallback className="bg-gradient-to-br from-blue-500 to-purple-600 text-white text-base sm:text-lg font-bold">
                    {video.channel.name[0]}
                  </AvatarFallback>
                </Avatar>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-1.5">
                    <h2 className="font-semibold text-sm sm:text-base truncate hover:text-primary cursor-pointer transition-colors" onClick={handleChannelClick}>
                      {video.channel.name}
                    </h2>
                    {video.channel.verified && (
                      <svg className="h-4 w-4 sm:h-5 sm:w-5 text-primary flex-shrink-0" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z"/>
                      </svg>
                    )}
                  </div>
                  <p className="text-xs sm:text-sm text-muted-foreground truncate">
                    {video.channel.subscribers?.toLocaleString()} subscribers
                  </p>
                </div>
              </div>
              <Button 
                variant={isSubscribed ? "secondary" : "default"} 
                className={`rounded-full px-5 py-2.5 sm:px-6 sm:py-2 h-11 sm:h-auto font-semibold transition-all flex-shrink-0 ${
                  isSubscribed 
                    ? 'hover:bg-secondary/80' 
                    : 'bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 shadow-lg shadow-red-500/30'
                }`}
                onClick={handleSubscribe}
                disabled={subscribeMutation.isPending}
              >
                {isSubscribed ? "Subscribed" : "Subscribe"}
              </Button>
            </div>

            {/* Action Buttons - Horizontal Scrollable on Mobile */}
            <div className="relative -mx-3 sm:mx-0">
              <div className="overflow-x-auto scrollbar-hide px-3 sm:px-0">
                <div className="flex items-center gap-2 sm:flex-wrap w-max sm:w-auto">
                  {/* Like/Dislike Group */}
                  <div className="flex items-center bg-secondary/50 rounded-full overflow-hidden shadow-sm hover:shadow-md transition-shadow flex-shrink-0">
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className={`rounded-none h-11 sm:h-auto px-4 sm:px-4 py-2.5 sm:py-2 ${userLike?.type === 'like' ? 'bg-primary/10 text-primary' : ''} hover:bg-primary/20 transition-all`}
                      onClick={handleLike}
                      disabled={likeMutation.isPending}
                    >
                      <ThumbsUp className={`h-5 w-5 sm:h-4 sm:w-4 mr-2 ${userLike?.type === 'like' ? 'fill-current' : ''}`} />
                      <span className="font-semibold text-sm">{likeCounts.likes}</span>
                    </Button>
                    <Separator orientation="vertical" className="h-6" />
                    <Button 
                      variant="ghost" 
                      size="sm" 
                      className={`rounded-none h-11 sm:h-auto px-4 sm:px-4 py-2.5 sm:py-2 ${userLike?.type === 'dislike' ? 'bg-primary/10 text-primary' : ''} hover:bg-primary/20 transition-all`}
                      onClick={handleDislike}
                      disabled={likeMutation.isPending}
                    >
                      <ThumbsDown className={`h-5 w-5 sm:h-4 sm:w-4 ${userLike?.type === 'dislike' ? 'fill-current' : ''}`} />
                    </Button>
                  </div>

                  <Button 
                    variant="secondary" 
                    size="sm" 
                    className="rounded-full h-11 sm:h-auto px-5 py-2.5 sm:px-5 sm:py-2 hover:bg-secondary/80 transition-all shadow-sm flex-shrink-0"
                    onClick={handleShare}
                  >
                    <Share2 className="h-5 w-5 sm:h-4 sm:w-4 mr-2" />
                    <span className="font-semibold text-sm">Share</span>
                  </Button>

                  <Button 
                    variant="secondary" 
                    size="sm" 
                    className="rounded-full h-11 sm:h-auto px-5 py-2.5 sm:px-5 sm:py-2 hover:bg-secondary/80 transition-all shadow-sm flex-shrink-0"
                    onClick={handleSave}
                  >
                    <Clock className="h-5 w-5 sm:h-4 sm:w-4 mr-2" />
                    <span className="font-semibold text-sm">Save</span>
                  </Button>

                  <Button variant="secondary" size="sm" className="rounded-full h-11 sm:h-auto px-4 py-2.5 sm:py-2 hover:bg-secondary/80 transition-all shadow-sm flex-shrink-0">
                    <MoreVertical className="h-5 w-5 sm:h-4 sm:w-4" />
                  </Button>
                </div>
              </div>
            </div>
          </div>

          {/* Description Section */}
          <div className="mx-3 sm:mx-0 bg-secondary/30 rounded-2xl p-3 sm:p-4 md:p-5 hover:bg-secondary/40 transition-colors">
            <div className="flex gap-2 sm:gap-4 text-xs sm:text-sm font-semibold mb-3 flex-wrap hidden sm:flex">
              <span className="text-foreground">{video.views?.toLocaleString()} views</span>
              <span className="text-muted-foreground">
                {new Date(video.uploadedAt || Date.now()).toLocaleDateString('en-US', { 
                  year: 'numeric', 
                  month: 'short', 
                  day: 'numeric' 
                })}
              </span>
              {video.category && (
                <span className="px-3 py-0.5 bg-primary/10 text-primary rounded-full text-xs font-medium">
                  {video.category}
                </span>
              )}
            </div>
            <div className={`text-sm leading-relaxed whitespace-pre-wrap ${descriptionExpanded ? '' : 'line-clamp-2'}`}>
              {video.description || "No description available."}
            </div>
            {video.description && video.description.length > 100 && (
              <Button 
                variant="ghost" 
                size="sm" 
                className="mt-2 text-foreground font-semibold hover:bg-transparent p-0 h-auto"
                onClick={() => setDescriptionExpanded(!descriptionExpanded)}
              >
                {descriptionExpanded ? (
                  <>Show less <ChevronUp className="ml-1 h-4 w-4" /></>
                ) : (
                  <>Show more <ChevronDown className="ml-1 h-4 w-4" /></>
                )}
              </Button>
            )}
          </div>

          {/* Comments Section */}
          <div className="space-y-4 sm:space-y-6 pb-6 sm:pb-8 px-3 sm:px-0">
            <div className="flex items-center gap-3 sm:gap-4">
              <h2 className="font-bold text-lg sm:text-xl">{comments.length} Comments</h2>
            </div>

            {/* Add Comment */}
            <div className="flex gap-3 sm:gap-4">
              <Avatar className="h-10 w-10 flex-shrink-0 ring-2 ring-background">
                <AvatarFallback className="bg-gradient-to-br from-blue-500 to-purple-600 text-white">
                  {currentUserId ? 'U' : 'G'}
                </AvatarFallback>
              </Avatar>
              <div className="flex-1 space-y-2 sm:space-y-3">
                <Textarea
                  value={commentText}
                  onChange={(e) => setCommentText(e.target.value)}
                  placeholder="Add a comment..."
                  className="min-h-[100px] resize-none border-2 focus-visible:ring-2 focus-visible:ring-primary rounded-xl"
                />
                <div className="flex justify-end gap-2">
                  <Button 
                    variant="ghost"
                    size="sm"
                    onClick={() => setCommentText("")}
                    className="rounded-full"
                  >
                    Cancel
                  </Button>
                  <Button 
                    onClick={handlePostComment}
                    disabled={createCommentMutation.isPending || !commentText.trim()}
                    className="rounded-full px-5"
                    size="sm"
                  >
                    <Send className="h-4 w-4 mr-2" />
                    {createCommentMutation.isPending ? "Posting..." : "Comment"}
                  </Button>
                </div>
              </div>
            </div>

            {/* Comments List */}
            <Separator />

            <div className="space-y-1">
              {comments.map((comment) => (
                <CommentItem key={comment.id} comment={comment} />
              ))}

              {comments.length === 0 && (
                <div className="text-center py-16">
                  <div className="inline-flex items-center justify-center w-16 h-16 rounded-full bg-muted mb-4">
                    <Send className="h-8 w-8 text-muted-foreground" />
                  </div>
                  <p className="text-muted-foreground font-medium">No comments yet</p>
                  <p className="text-sm text-muted-foreground mt-1">Be the first to share your thoughts!</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Sidebar - Related Videos */}
        <div className="space-y-2 sm:space-y-3 w-full overflow-x-hidden px-3 sm:px-0">
          <div className="xl:sticky top-4 space-y-2 sm:space-y-3">
            <h2 className="font-bold text-base sm:text-lg md:text-xl">Related Videos</h2>
            <div className="space-y-2 sm:space-y-3">
              {relatedVideos.map((relatedVideo) => (
                <div 
                  key={relatedVideo.id} 
                  className="group flex gap-2 sm:gap-3 cursor-pointer hover:bg-secondary/30 p-2 rounded-xl transition-all duration-200"
                  onClick={() => handleRelatedVideoClick(relatedVideo.id)}
                >
                  <div className="w-[120px] sm:w-[140px] xl:w-[168px] flex-shrink-0 relative rounded-lg overflow-hidden">
                    <img
                      src={relatedVideo.thumbnail}
                      alt={relatedVideo.title}
                      className="w-full aspect-video object-cover group-hover:scale-105 transition-transform duration-200"
                    />
                    {relatedVideo.duration && !relatedVideo.isLive && (
                      <div className="absolute bottom-1.5 right-1.5 bg-black/90 text-white text-xs px-1.5 py-0.5 rounded font-semibold">
                        {relatedVideo.duration}
                      </div>
                    )}
                    {relatedVideo.isLive && (
                      <div className="absolute bottom-1.5 right-1.5 bg-red-600 text-white text-xs px-2 py-0.5 rounded font-bold uppercase">
                        Live
                      </div>
                    )}
                  </div>
                  <div className="flex-1 min-w-0">
                    <h3 className="text-sm font-semibold line-clamp-2 mb-1.5 group-hover:text-primary transition-colors leading-snug">
                      {relatedVideo.title}
                    </h3>
                    <div className="space-y-0.5">
                      <p className="text-xs text-muted-foreground hover:text-foreground transition-colors font-medium">
                        {relatedVideo.channel.name}
                        {relatedVideo.channel.verified && (
                          <svg className="inline h-3 w-3 ml-1 text-primary" viewBox="0 0 24 24" fill="currentColor">
                            <path d="M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z"/>
                          </svg>
                        )}
                      </p>
                      <div className="flex items-center gap-1 text-xs text-muted-foreground">
                        <span>{relatedVideo.views?.toLocaleString()} views</span>
                        <span>â€¢</span>
                        <span>{formatDistanceToNow(new Date(relatedVideo.uploadedAt || Date.now()), { addSuffix: true })}</span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}

              {relatedVideos.length === 0 && (
                <div className="text-center py-8 text-muted-foreground">
                  <p className="text-sm">No related videos found</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Save to Playlist Dialog */}
      <Dialog open={saveDialogOpen} onOpenChange={setSaveDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Save to playlist</DialogTitle>
            <DialogDescription>
              Choose a playlist or create a new one
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4 py-4">
            {/* Playlist List */}
            <div className="space-y-2 max-h-[300px] overflow-y-auto">
              {userPlaylists.length === 0 ? (
                <p className="text-sm text-muted-foreground text-center py-4">
                  No playlists yet. Create one below!
                </p>
              ) : (
                userPlaylists.map((playlist: any) => (
                  <button
                    key={playlist.id}
                    onClick={() => addToPlaylistMutation.mutate(playlist.id)}
                    disabled={addToPlaylistMutation.isPending}
                    className="w-full flex items-center gap-3 p-3 rounded-lg hover:bg-secondary/50 transition-colors text-left"
                  >
                    <div className="flex-1">
                      <p className="font-medium">{playlist.name}</p>
                      <p className="text-xs text-muted-foreground">
                        {playlist.isPublic ? 'Public' : 'Private'}
                      </p>
                    </div>
                  </button>
                ))
              )}
            </div>

            {/* Create New Playlist */}
            <div className="border-t pt-4 space-y-2">
              <p className="text-sm font-medium">Create new playlist</p>
              <div className="flex gap-2">
                <Input
                  placeholder="Playlist name"
                  value={newPlaylistName}
                  onChange={(e) => setNewPlaylistName(e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      handleCreatePlaylist();
                    }
                  }}
                />
                <Button 
                  onClick={handleCreatePlaylist}
                  disabled={!newPlaylistName.trim() || createPlaylistMutation.isPending}
                >
                  Create
                </Button>
              </div>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

