    bottom: 8px;
    right: 8px;
    background: rgba(0, 0, 0, 0.8);
    padding: 2px 4px;
    border-radius: 2px;
    font-size: 12px;
    font-weight: 500;
  }

  .live-badge {
    position: absolute;
    bottom: 8px;
    left: 8px;
    background: hsl(0, 84%, 60%);
    padding: 2px 6px;
    border-radius: 2px;
    font-size: 11px;
    font-weight: 700;
    text-transform: uppercase;
  }

  .nav-item {
    transition: background-color 0.2s, color 0.2s;
  }

  .nav-item:hover {
    background-color: hsl(0, 0%, 18%);
  }

  .nav-item.active {
    background-color: hsl(0, 0%, 18%);
    color: hsl(200, 100%, 62%);
  }

  .video-card {
    transition: transform 0.2s;
  }

  .video-card:hover {
