  .channel-avatar {
    width: 36px;
    height: 36px;
    border-radius: 50%;
    background: hsl(0, 0%, 30%);
    overflow: hidden;
  }

  .verified-badge {
    color: hsl(0, 0%, 67%);
    font-size: 12px;
  }

  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
}

