import { useQuery } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import VideoCard from "@/components/VideoCard";
import { VideoWithChannel } from "@shared/schema";
import type { Channel } from "@shared/schema";

export default function ChannelPage() {
  const [, params] = useRoute("/channel/:id");
  const channelId = params?.id;

  const { data: channel, isLoading: channelLoading } = useQuery<Channel>({
    queryKey: ["/api/channels", channelId],
    queryFn: async () => {
      const response = await fetch(`/api/channels/${channelId}`);
      if (!response.ok) throw new Error('Failed to fetch channel');
      return response.json();
    },
    enabled: !!channelId
  });

  const { data: videos = [] } = useQuery<VideoWithChannel[]>({
    queryKey: ["/api/videos/channel", channelId],
    queryFn: async () => {
      const response = await fetch(`/api/videos/by-channels?channelIds=${channelId}`);
      if (!response.ok) return [];
      return response.json();
    },
    enabled: !!channelId
  });

  if (channelLoading || !channel) {
    return (
      <div className="p-4 sm:p-6 space-y-4 sm:space-y-6">
        <div className="h-32 sm:h-40 md:h-48 bg-muted animate-pulse rounded-xl"></div>
        <div className="flex flex-col sm:flex-row items-center sm:items-start gap-4 sm:gap-6">
          <div className="h-24 w-24 sm:h-28 sm:w-28 md:h-32 md:w-32 bg-muted animate-pulse rounded-full"></div>
          <div className="flex-1 space-y-3 w-full">
            <div className="h-8 bg-muted animate-pulse rounded w-1/3 mx-auto sm:mx-0"></div>
            <div className="h-4 bg-muted animate-pulse rounded w-1/4 mx-auto sm:mx-0"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 sm:p-6 space-y-4 sm:space-y-6">
      <div className="h-32 sm:h-40 md:h-48 bg-gradient-to-br from-primary/20 to-primary/5 rounded-xl"></div>

      <div className="flex flex-col sm:flex-row items-center sm:items-start gap-4 sm:gap-6">
        <Avatar className="h-24 w-24 sm:h-28 sm:w-28 md:h-32 md:w-32 border-2 sm:border-4 border-background">
          <AvatarImage src={channel.avatar || undefined} />
          <AvatarFallback className="text-4xl">{channel.name[0]}</AvatarFallback>
        </Avatar>

        <div className="flex-1 w-full sm:w-auto">
          <div className="flex items-center gap-2 mb-2 justify-center sm:justify-start">
            <h1 className="text-xl sm:text-2xl md:text-3xl font-bold text-center sm:text-left">{channel.name}</h1>
            {channel.verified && (
              <svg className="h-6 w-6 text-primary" viewBox="0 0 24 24" fill="currentColor">
                <path d="M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41z"/>
              </svg>
            )}
          </div>
          <p className="text-sm sm:text-base text-muted-foreground mb-1 text-center sm:text-left">@{channel.username}</p>
          <p className="text-sm sm:text-base text-muted-foreground mb-4 text-center sm:text-left">
            {channel.subscribers?.toLocaleString()} subscribers â€¢ {videos.length} videos
          </p>
          <p className="text-sm mb-4 text-center sm:text-left">{channel.description || "No description available."}</p>
          <div className="flex gap-2 flex-wrap justify-center sm:justify-start">
            <Button>Subscribe</Button>
            <Button variant="outline">Joined</Button>
          </div>
        </div>
      </div>

      <Tabs defaultValue="home" className="w-full">
        <TabsList className="w-full justify-start border-b rounded-none h-auto p-0 bg-transparent overflow-x-auto">
          <TabsTrigger value="home" className="rounded-none border-b-2 border-transparent data-[state=active]:border-foreground data-[state=active]:bg-transparent">Home</TabsTrigger>
          <TabsTrigger value="videos" className="rounded-none border-b-2 border-transparent data-[state=active]:border-foreground data-[state=active]:bg-transparent">Videos</TabsTrigger>
          <TabsTrigger value="shorts" className="rounded-none border-b-2 border-transparent data-[state=active]:border-foreground data-[state=active]:bg-transparent">Shorts</TabsTrigger>
          <TabsTrigger value="posts" className="rounded-none border-b-2 border-transparent data-[state=active]:border-foreground data-[state=active]:bg-transparent">Posts</TabsTrigger>
          <TabsTrigger value="about" className="rounded-none border-b-2 border-transparent data-[state=active]:border-foreground data-[state=active]:bg-transparent">About</TabsTrigger>
        </TabsList>

        <TabsContent value="home" className="mt-6">
          {videos.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-muted-foreground">No videos yet</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
              {videos.slice(0, 8).map((video) => (
                <VideoCard
                  key={video.id}
                  video={video}
                  onClick={() => console.log('Play video:', video.id)}
                />
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="videos" className="mt-6">
          {videos.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-muted-foreground">No videos yet</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
              {videos.map((video) => (
                <VideoCard
                  key={video.id}
                  video={video}
                  onClick={() => console.log('Play video:', video.id)}
                />
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="shorts" className="mt-6">
          <div className="text-center py-12">
            <p className="text-muted-foreground">No shorts yet</p>
          </div>
        </TabsContent>

        <TabsContent value="posts" className="mt-6">
          <div className="text-center py-12">
            <p className="text-muted-foreground">No posts yet</p>
          </div>
        </TabsContent>

        <TabsContent value="about" className="mt-6">
          <div className="space-y-4">
            <div>
              <h3 className="font-semibold mb-2">Description</h3>
              <p className="text-sm text-muted-foreground">
                {channel.description || "No description available."}
              </p>
            </div>
            <div>
              <h3 className="font-semibold mb-2">Stats</h3>
              <ul className="text-sm text-muted-foreground space-y-1">
                <li>{channel.subscribers?.toLocaleString()} subscribers</li>
                <li>{videos.length} videos</li>
              </ul>
            </div>
          </div>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/Landing.tsx.content:
import { Button } from "@/components/ui/button";
import { Play, Video, Zap, Shield, Mail } from "lucide-react";
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";

export default function Landing() {
  const [showEmailSignup, setShowEmailSignup] = useState(false);
  const [step, setStep] = useState<'email' | 'otp'>('email');
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const { toast } = useToast();

  const sendOtpMutation = useMutation({
    mutationFn: async (email: string) => {
      const res = await fetch('/api/auth/email/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      if (!res.ok) throw new Error('Failed to send OTP');
      return res.json();
    },
    onSuccess: () => {
      setStep('otp');
      toast({
        title: "OTP Sent",
        description: "Check your email for the verification code (or enter any code for now)",
      });
    },
  });

  const verifyOtpMutation = useMutation({
    mutationFn: async () => {
      const res = await fetch('/api/auth/email/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, otp, firstName, lastName }),
      });
      if (!res.ok) throw new Error('Failed to verify OTP');
      return res.json();
    },
    onSuccess: () => {
      toast({
        title: "Success!",
        description: "Account created successfully",
      });
      window.location.href = '/';
    },
  });

  return (
    <div className="min-h-screen bg-background">
      <nav className="border-b border-border">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Play className="w-8 h-8 text-primary" />
            <span className="text-2xl font-bold">CineWeave</span>
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-4">
        <section className="py-20 text-center">
          <h1 className="text-5xl font-bold mb-6">
            Your Videos, Your Way
          </h1>
          <p className="text-xl text-muted-foreground mb-8 max-w-2xl mx-auto">
            Experience video streaming with powerful personalization. Block unwanted content, 
            organize your subscriptions, and enjoy a truly customized viewing experience.
          </p>
          <div className="flex gap-4 justify-center">
            <Button 
              size="lg" 
              onClick={() => window.location.href = "/api/login"}
              data-testid="button-google-signup"
            >
              Sign up with Google
            </Button>
            <Button 
              size="lg" 
              variant="outline"
              onClick={() => setShowEmailSignup(true)}
              data-testid="button-email-signup"
            >
              <Mail className="w-4 h-4 mr-2" />
              Sign up with Email
            </Button>
          </div>
        </section>

        <Dialog open={showEmailSignup} onOpenChange={setShowEmailSignup}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>{step === 'email' ? 'Sign up with Email' : 'Enter OTP'}</DialogTitle>
              <DialogDescription>
                {step === 'email' 
                  ? 'Enter your email to receive a verification code'
                  : 'Enter the OTP sent to your email (any code works for now)'}
              </DialogDescription>
            </DialogHeader>

            {step === 'email' ? (
              <div className="space-y-4">
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="your@email.com"
                  />
                </div>
                <div>
                  <Label htmlFor="firstName">First Name (Optional)</Label>
                  <Input
                    id="firstName"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    placeholder="John"
                  />
                </div>
                <div>
                  <Label htmlFor="lastName">Last Name (Optional)</Label>
                  <Input
                    id="lastName"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    placeholder="Doe"
                  />
                </div>
                <Button 
                  className="w-full" 
                  onClick={() => sendOtpMutation.mutate(email)}
                  disabled={!email || sendOtpMutation.isPending}
                >
                  {sendOtpMutation.isPending ? 'Sending...' : 'Send OTP'}
                </Button>
              </div>
            ) : (
              <div className="space-y-4">
                <div>
                  <Label htmlFor="otp">OTP Code</Label>
                  <Input
                    id="otp"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    placeholder="Enter any code"
                  />
                </div>
                <Button 
                  className="w-full" 
                  onClick={() => verifyOtpMutation.mutate()}
                  disabled={!otp || verifyOtpMutation.isPending}
                >
                  {verifyOtpMutation.isPending ? 'Verifying...' : 'Verify & Sign Up'}
                </Button>
                <Button 
                  variant="ghost" 
                  className="w-full" 
                  onClick={() => setStep('email')}
                >
                  Back
                </Button>
              </div>
            )}
          </DialogContent>
        </Dialog>

        <section className="py-20 grid md:grid-cols-3 gap-12">
          <div className="text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-primary/10 rounded-full flex items-center justify-center">
              <Video className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-xl font-semibold mb-2">Personal Mode</h3>
            <p className="text-muted-foreground">
              Toggle to see only videos from channels you subscribe to. No algorithms, no distractions.
            </p>
          </div>

          <div className="text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-primary/10 rounded-full flex items-center justify-center">
              <Shield className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-xl font-semibold mb-2">Content Control</h3>
            <p className="text-muted-foreground">
              Permanently block channels and never see their content again. Full control over what you watch.
            </p>
          </div>

          <div className="text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-primary/10 rounded-full flex items-center justify-center">
              <Zap className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-xl font-semibold mb-2">Smart Spaces</h3>
            <p className="text-muted-foreground">
              Organize subscriptions into custom collections. Create spaces for gaming, tech, entertainment, and more.
            </p>
          </div>
        </section>
      </main>
    </div>
  );
}
