    return result[0];
  }

  async getCommentsByVideo(videoId: string, limit: number = 50, offset: number = 0, sortBy: string = 'createdAt'): Promise<Comment[]> {
    try {
      let query = db
        .select()
        .from(comments)
        .where(eq(comments.videoId, videoId))
