              <div className="flex gap-3">
                <div className="w-9 h-9 bg-muted animate-pulse rounded-full"></div>
                <div className="flex-1 space-y-2">
                  <div className="h-4 bg-muted animate-pulse rounded"></div>
                  <div className="h-3 bg-muted animate-pulse rounded w-2/3"></div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Category Filter Chips */}
      <div className="flex items-center gap-3 overflow-x-auto pb-2">
        {categoryFilters.map((filter) => (
          <Button
            key={filter.value}
            variant={activeCategory === filter.value ? "default" : "secondary"}
            size="sm"
            onClick={() => setActiveCategory(filter.value)}
            className={`whitespace-nowrap ${
              activeCategory === filter.value 
                ? "bg-foreground text-background hover:bg-foreground/90" 
                : "bg-muted text-foreground hover:bg-secondary"
            }`}
            data-testid={`filter-${filter.value || 'all'}`}
          >
            {filter.label}
          </Button>
        ))}
      </div>

      {/* Search Results Header */}
      {searchQuery && (
        <div className="mb-6">
          <h2 className="text-xl font-semibold text-foreground mb-2">
            Search results for "{searchQuery}"
          </h2>
          {videos.length === 0 && (
            <p className="text-muted-foreground">No videos found matching your search.</p>
          )}
        </div>
      )}

      {/* Personal Mode Empty State */}
      {personalMode && videos.length === 0 && !searchQuery && (
        <div className="text-center py-12">
          <h2 className="text-xl font-semibold text-foreground mb-2">No subscription content</h2>
          <p className="text-muted-foreground">
            Subscribe to channels to see their content in Personal Mode.
          </p>
        </div>
      )}

      {/* Top Video Grid (First 2-3 rows = 8-12 videos) */}
      {videos.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
          {videos.slice(0, 12).map((video) => (
            <VideoCard
              key={video.id}
              video={video}
              onClick={() => handleVideoClick(video)}
            />
          ))}
        </div>
      )}

      {/* Spaces Section */}
      {!searchQuery && videos.length > 0 && (
        <div>
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-foreground flex items-center gap-2">
              <span className="text-2xl">📁</span>
              Your Spaces
            </h2>
            <Button
              variant="ghost"
              size="sm"
              className="text-primary hover:underline"
              onClick={() => window.location.href = "/spaces"}
              data-testid="button-view-all-spaces"
            >
              View all →
            </Button>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {spaces.slice(0, 3).map((space) => (
              <SpaceCard
                key={space.id}
                space={space}
                onClick={() => handleSpaceClick(space)}
              />
            ))}

            {/* Create New Space CTA */}
            <div
              className="p-4 border-2 border-dashed border-border rounded-xl flex items-center justify-center hover:border-primary transition cursor-pointer"
              onClick={handleCreateSpace}
              data-testid="button-create-space"
            >
              <div className="text-center">
                <Plus className="h-8 w-8 text-primary mx-auto mb-2" />
                <p className="text-sm font-medium text-foreground">Create New Space</p>
                <p className="text-xs text-muted-foreground">Organize your favorite channels</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Remaining Videos (After Spaces) */}
      {videos.length > 12 && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
          {videos.slice(12).map((video) => (
            <VideoCard
              key={video.id}
              video={video}
              onClick={() => handleVideoClick(video)}
            />
          ))}
        </div>
      )}

      {/* Channel Creation Dialog */}
      <ChannelCreationDialog 
        open={showChannelCreation}
        onOpenChange={setShowChannelCreation}
      />
    </div>
  );
}

client/src/pages/KeyboardShortcuts.tsx.content:
import { Keyboard } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";

interface Shortcut {
  keys: string[];
  description: string;
}

interface ShortcutSection {
  title: string;
  shortcuts: Shortcut[];
}

const shortcutSections: ShortcutSection[] = [
  {
    title: "Video Player",
    shortcuts: [
      { keys: ["Space", "K"], description: "Play/Pause" },
      { keys: ["←"], description: "Rewind 5 seconds" },
      { keys: ["→"], description: "Fast forward 5 seconds" },
      { keys: ["↑"], description: "Increase volume" },
      { keys: ["↓"], description: "Decrease volume" },
      { keys: ["M"], description: "Mute/Unmute" },
      { keys: ["F"], description: "Toggle fullscreen" },
      { keys: ["C"], description: "Toggle captions" },
      { keys: ["J"], description: "Rewind 10 seconds" },
      { keys: ["L"], description: "Fast forward 10 seconds" },
      { keys: ["0-9"], description: "Jump to 0%-90% of video" },
      { keys: ["<"], description: "Decrease playback speed" },
      { keys: [">"], description: "Increase playback speed" }
    ]
  },
  {
    title: "Navigation",
    shortcuts: [
      { keys: ["Home"], description: "Go to home page" },
      { keys: ["/"], description: "Focus search box" },
      { keys: ["Esc"], description: "Close overlays/menus" },
      { keys: ["Shift", "?"], description: "Show keyboard shortcuts" }
    ]
  },
  {
    title: "General",
    shortcuts: [
      { keys: ["Ctrl", "K"], description: "Open search" },
      { keys: ["N"], description: "Toggle notifications" },
      { keys: ["S"], description: "Toggle sidebar" },
      { keys: ["P"], description: "Toggle personal mode" }
    ]
  }
];

export default function KeyboardShortcuts() {
  return (
    <div className="max-w-5xl mx-auto p-6">
      <div className="mb-8">
        <div className="flex items-center gap-3 mb-2">
          <Keyboard className="h-8 w-8 text-primary" />
          <h1 className="text-3xl font-bold">Keyboard Shortcuts</h1>
        </div>
        <p className="text-muted-foreground">
          Use these keyboard shortcuts to navigate and control CineWeave more efficiently
        </p>
      </div>

      <div className="space-y-6">
        {shortcutSections.map((section) => (
          <Card key={section.title}>
            <CardHeader>
              <CardTitle>{section.title}</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {section.shortcuts.map((shortcut, index) => (
                  <div
                    key={index}
                    className="flex items-center justify-between py-2 border-b last:border-b-0"
                  >
                    <span className="text-sm">{shortcut.description}</span>
                    <div className="flex gap-1">
                      {shortcut.keys.map((key, keyIndex) => (
                        <span key={keyIndex} className="flex items-center gap-1">
                          <kbd className="px-3 py-1.5 text-xs font-semibold text-foreground bg-muted border border-border rounded shadow-sm">
                            {key}
                          </kbd>
                          {keyIndex < shortcut.keys.length - 1 && (
                            <span className="text-muted-foreground text-xs">+</span>
                          )}
                        </span>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      <div className="mt-8 p-4 bg-muted rounded-lg">
        <p className="text-sm text-muted-foreground">
          <strong>Tip:</strong> Press <kbd className="px-2 py-1 text-xs font-semibold bg-background border rounded">Shift</kbd> + <kbd className="px-2 py-1 text-xs font-semibold bg-background border rounded">?</kbd> to quickly access this list while browsing.
        </p>
      </div>
    </div>
  );
}

client/src/pages/Landing.tsx.content:
import { Button } from "@/components/ui/button";
import { Play, Video, Zap, Shield, Mail } from "lucide-react";
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";

export default function Landing() {
  const [showEmailSignup, setShowEmailSignup] = useState(false);
  const [step, setStep] = useState<'email' | 'otp'>('email');
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const { toast } = useToast();

  const sendOtpMutation = useMutation({
    mutationFn: async (email: string) => {
      const res = await fetch('/api/auth/email/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      if (!res.ok) throw new Error('Failed to send OTP');
      return res.json();
    },
    onSuccess: () => {
      setStep('otp');
      toast({
        title: "OTP Sent",
        description: "Check your email for the verification code (or enter any code for now)",
      });
    },
  });

  const verifyOtpMutation = useMutation({
    mutationFn: async () => {
      const res = await fetch('/api/auth/email/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, otp, firstName, lastName }),
      });
      if (!res.ok) throw new Error('Failed to verify OTP');
      return res.json();
    },
    onSuccess: () => {
      toast({
        title: "Success!",
        description: "Account created successfully",
      });
      window.location.href = '/';
    },
  });

  return (
    <div className="min-h-screen bg-background">
      <nav className="border-b border-border">
        <div className="container mx-auto px-4 py-4 flex items-center justify-between">
          <div className="flex items-center gap-2">
            <Play className="w-8 h-8 text-primary" />
            <span className="text-2xl font-bold">CineWeave</span>
          </div>
        </div>
      </nav>

      <main className="container mx-auto px-4">
        <section className="py-20 text-center">
          <h1 className="text-5xl font-bold mb-6">
            Your Videos, Your Way
          </h1>
          <p className="text-xl text-muted-foreground mb-8 max-w-2xl mx-auto">
            Experience video streaming with powerful personalization. Block unwanted content, 
            organize your subscriptions, and enjoy a truly customized viewing experience.
          </p>
          <div className="flex gap-4 justify-center">
            <Button 
              size="lg" 
              onClick={() => window.location.href = "/api/login"}
              data-testid="button-google-signup"
            >
              Sign up with Google
            </Button>
            <Button 
              size="lg" 
              variant="outline"
              onClick={() => setShowEmailSignup(true)}
              data-testid="button-email-signup"
            >
              <Mail className="w-4 h-4 mr-2" />
              Sign up with Email
            </Button>
          </div>
        </section>

        <Dialog open={showEmailSignup} onOpenChange={setShowEmailSignup}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>{step === 'email' ? 'Sign up with Email' : 'Enter OTP'}</DialogTitle>
              <DialogDescription>
                {step === 'email' 
                  ? 'Enter your email to receive a verification code'
                  : 'Enter the OTP sent to your email (any code works for now)'}
              </DialogDescription>
            </DialogHeader>

            {step === 'email' ? (
              <div className="space-y-4">
                <div>
                  <Label htmlFor="email">Email</Label>
                  <Input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="your@email.com"
                  />
                </div>
                <div>
                  <Label htmlFor="firstName">First Name (Optional)</Label>
                  <Input
                    id="firstName"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                    placeholder="John"
                  />
                </div>
                <div>
                  <Label htmlFor="lastName">Last Name (Optional)</Label>
                  <Input
                    id="lastName"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                    placeholder="Doe"
                  />
                </div>
                <Button 
                  className="w-full" 
                  onClick={() => sendOtpMutation.mutate(email)}
                  disabled={!email || sendOtpMutation.isPending}
                >
                  {sendOtpMutation.isPending ? 'Sending...' : 'Send OTP'}
                </Button>
              </div>
            ) : (
              <div className="space-y-4">
                <div>
                  <Label htmlFor="otp">OTP Code</Label>
                  <Input
                    id="otp"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    placeholder="Enter any code"
                  />
                </div>
                <Button 
                  className="w-full" 
                  onClick={() => verifyOtpMutation.mutate()}
                  disabled={!otp || verifyOtpMutation.isPending}
                >
                  {verifyOtpMutation.isPending ? 'Verifying...' : 'Verify & Sign Up'}
                </Button>
                <Button 
                  variant="ghost" 
                  className="w-full" 
                  onClick={() => setStep('email')}
                >
                  Back
                </Button>
              </div>
            )}
          </DialogContent>
        </Dialog>

        <section className="py-20 grid md:grid-cols-3 gap-12">
          <div className="text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-primary/10 rounded-full flex items-center justify-center">
              <Video className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-xl font-semibold mb-2">Personal Mode</h3>
            <p className="text-muted-foreground">
              Toggle to see only videos from channels you subscribe to. No algorithms, no distractions.
            </p>
          </div>

          <div className="text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-primary/10 rounded-full flex items-center justify-center">
              <Shield className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-xl font-semibold mb-2">Content Control</h3>
            <p className="text-muted-foreground">
              Permanently block channels and never see their content again. Full control over what you watch.
            </p>
          </div>

          <div className="text-center">
            <div className="w-16 h-16 mx-auto mb-4 bg-primary/10 rounded-full flex items-center justify-center">
              <Zap className="w-8 h-8 text-primary" />
            </div>
            <h3 className="text-xl font-semibold mb-2">Smart Spaces</h3>
            <p className="text-muted-foreground">
              Organize subscriptions into custom collections. Create spaces for gaming, tech, entertainment, and more.
            </p>
          </div>
        </section>
      </main>
    </div>
  );
}

