        <TabsContent value="country" className="space-y-4">
          <Input
            type="text"
            placeholder="Search countries..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />

          <div className="space-y-2 max-h-[500px] overflow-y-auto pr-2">
            {filteredCountries.map((country) => (
              <div
                key={country.code}
